
public class Exccer {

	public static void main(String[] args) {
		int a,b,c;

		for (a=1;a<=7;a++)
		{
			for (b=1;b<=a;b++)
			{
				System.out.print(b);
			}
			for (c=7-a;c>=1;c--)//6
			{
				System.out.print("x");
			}
			System.out.println();

			// TODO Auto-generated method stub

		}}

}

