
public class Forrttt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,c;
		for(a=1;a<=4;a++)
		{ 
			for(b=0;b<=3-a;b++)
				System.out.print(".");
			
				for (c=1;c<=a;c++)
				System.out.print("x");
			System.out.println();
			
		}
			
		

	}

}
